import axios from 'axios';

const apiUrl = "https://localhost:5073"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  addTask: async(name)=>{
    console.log('addTask', name)
    const result = await axios.put(`${apiUrl}/items`, name);
    console.log("add result.data :",result.data);

    return result.data;
  },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    const result = await axios.post(`${apiUrl}/items/${id}`, isComplete);
    console.log("set result.data :",result.data);

    return result.data;
  },

  deleteTask:async(id)=>{
    console.log('deleteTask')
    const result = await axios.delete(`${apiUrl}/items/${id}`);
    console.log("delete result.data :",result.data);

    return result.data;
  }
};
